import React, { useState } from 'react';
import { Form, Select, InputNumber, Card, Row, Col, Divider, Typography } from 'antd';
import { 
  EnvironmentOutlined, 
  UserOutlined, 
  BranchesOutlined, 
  ColumnHeightOutlined, 
  DashboardOutlined 
} from '@ant-design/icons';
import { UserInputs, BoardRecommendation, RidingStyle } from '../types/snowboard';

const { Title, Text } = Typography;

const SnowboardSelector: React.FC = () => {
  const [form] = Form.useForm();
  const [recommendation, setRecommendation] = useState<BoardRecommendation | null>(null);

  // Ë®àÁÆóÊé®Ëñ¶Èï∑Â∫¶ÁØÑÂúç
  const calculateLengthRange = (inputs: UserInputs): [number, number] => {
    const { region, gender, height, weight } = inputs;

    if (region === 'japan') {
      // Êó•Êú¨Âú∞ÂçÄÔºàË∫´È´òÁÇ∫‰∏ªÔºâ
      const baseLength = height * 0.9;
      return gender === 'male' 
        ? [baseLength - 5, baseLength + 5]
        : [baseLength - 10, baseLength];
    } else {
      // Âè∞ÁÅ£Âú∞ÂçÄÔºàÈ´îÈáçÁÇ∫‰∏ªÔºâ
      // ‰øÆÊ≠£ÂÖ¨ÂºèÔºöÂ∞áË®àÁÆóÁµêÊûúÈô§‰ª•2
      const baseLength = (weight * 2.5 + 110) / 2;
      return gender === 'male'
        ? [baseLength - 5, baseLength + 5]
        : [baseLength - 10, baseLength];
    }
  };

  // Áç≤ÂèñÊé®Ëñ¶ÊùøÂûã
  const getBoardProfile = (style: RidingStyle): string => {
    const profiles: Record<RidingStyle, string> = {
      'all-mountain': 'Hybrid (Ê∑∑ÂêàÂûã)',
      'freestyle': 'Rocker (ÂèçÊã±) Êàñ Flat (Âπ≥Â∫ï)',
      'carving': 'Camber (Ê≠£Êã±)'
    };
    return profiles[style];
  };

  // Áç≤ÂèñÊé®Ëñ¶Á°¨Â∫¶
  const getFlexRange = (style: RidingStyle): [number, number] => {
    const flexRanges: Record<RidingStyle, [number, number]> = {
      'all-mountain': [5, 7],
      'freestyle': [1, 5],
      'carving': [6, 10]
    };
    return flexRanges[style];
  };

  const onValuesChange = () => {
    const values = form.getFieldsValue();
    if (values.region && values.gender && values.height && values.weight && values.ridingStyle) {
      const lengthRange = calculateLengthRange(values);
      const [flexMin, flexMax] = getFlexRange(values.ridingStyle);

      // Ë®àÁÆó‰∏≠ÈñìÂÄº
      const avgLength = (lengthRange[0] + lengthRange[1]) / 2;
      const avgFlex = (flexMin + flexMax) / 2;

      setRecommendation({
        lengthRange: {
          min: lengthRange[0],
          max: lengthRange[1],
          avg: avgLength
        },
        profile: getBoardProfile(values.ridingStyle),
        flex: {
          min: flexMin,
          max: flexMax,
          avg: avgFlex
        }
      });
    }
  };

  return (
    <Row justify="center">
      <Col xs={24} sm={20} md={16} lg={12}>
        <Card
          title={
            <div style={{ textAlign: 'center' }}>
              <span role="img" aria-label="snowboarder" style={{ fontSize: '24px', marginRight: '8px' }}>üèÇ</span>
              <Title level={3} style={{ margin: 0, display: 'inline' }}>ÊªëÈõ™ÊùøÈÅ∏ÊìáÂô®</Title>
              <span role="img" aria-label="snowy mountain" style={{ fontSize: '24px', marginLeft: '8px' }}>üèîÔ∏è</span>
            </div>
          }
          style={{ boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}
        >
          <Form
            form={form}
            layout="vertical"
            onValuesChange={onValuesChange}
          >
            <Form.Item
              name="region"
              label={<><EnvironmentOutlined /> ÈÅ∏ÊìáÂú∞ÂçÄ</>}
              rules={[{ required: true, message: 'Ë´ãÈÅ∏ÊìáÂú∞ÂçÄ' }]}
            >
              <Select>
                <Select.Option value="japan">Êó•Êú¨Ôºà‰ª•Ë∫´È´òÁÇ∫‰∏ªÔºâ</Select.Option>
                <Select.Option value="taiwan">Âè∞ÁÅ£Ôºà‰ª•È´îÈáçÁÇ∫‰∏ªÔºâ</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="gender"
              label={<><UserOutlined /> ÊÄßÂà•</>}
              rules={[{ required: true, message: 'Ë´ãÈÅ∏ÊìáÊÄßÂà•' }]}
            >
              <Select>
                <Select.Option value="male">Áî∑ÊÄß</Select.Option>
                <Select.Option value="female">Â•≥ÊÄß</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="ridingStyle"
              label={<><BranchesOutlined /> ÊªëÈõ™È¢®Ê†º</>}
              rules={[{ required: true, message: 'Ë´ãÈÅ∏ÊìáÊªëÈõ™È¢®Ê†º' }]}
            >
              <Select>
                <Select.Option value="all-mountain">ÈÄöÁî® (All-Mountain)</Select.Option>
                <Select.Option value="freestyle">Âπ≥Ëä± (Freestyle)</Select.Option>
                <Select.Option value="carving">ÂàªÊªë (Carving)</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="height"
              label={<><ColumnHeightOutlined /> Ë∫´È´ò (cm)</>}
              rules={[
                { required: true, message: 'Ë´ãËº∏ÂÖ•Ë∫´È´ò' },
                { type: 'number', min: 140, max: 200, message: 'Ë∫´È´òÊáâÂú®140-200cm‰πãÈñì' }
              ]}
            >
              <InputNumber style={{ width: '100%' }} />
            </Form.Item>

            <Form.Item
              name="weight"
              label={<><DashboardOutlined /> È´îÈáç (kg)</>}
              rules={[
                { required: true, message: 'Ë´ãËº∏ÂÖ•È´îÈáç' },
                { type: 'number', min: 40, max: 120, message: 'È´îÈáçÊáâÂú®40-120kg‰πãÈñì' }
              ]}
            >
              <InputNumber style={{ width: '100%' }} />
            </Form.Item>
          </Form>

          {recommendation && (
            <>
              <Divider>
                <span role="img" aria-label="snowflake" style={{ fontSize: '20px' }}>‚ùÑÔ∏è</span> Êé®Ëñ¶ÁµêÊûú <span role="img" aria-label="snowflake" style={{ fontSize: '20px' }}>‚ùÑÔ∏è</span>
              </Divider>
              <Card 
                type="inner" 
                style={{ 
                  background: 'linear-gradient(135deg, #e6f7ff 0%, #ffffff 100%)',
                  border: '1px solid #91d5ff'
                }}
              >
                <Row gutter={[16, 16]}>
                  <Col span={24}>
                    <Text strong>Âª∫Ë≠∞Èõ™ÊùøÈï∑Â∫¶Ôºö</Text>
                    <Text>{recommendation.lengthRange.min.toFixed(1)} - {recommendation.lengthRange.max.toFixed(1)} cm</Text>
                    <Text type="secondary">ÔºàÊé®Ëñ¶‰∏≠ÈñìÂÄºÔºö{recommendation.lengthRange.avg.toFixed(1)} cmÔºâ</Text>
                  </Col>
                  <Col span={24}>
                    <Text strong>Âª∫Ë≠∞ÊùøÂûãÔºö</Text>
                    <Text>{recommendation.profile}</Text>
                  </Col>
                  <Col span={24}>
                    <Text strong>Âª∫Ë≠∞Á°¨Â∫¶Ôºö</Text>
                    <Text>{recommendation.flex.min} - {recommendation.flex.max}</Text>
                    <Text type="secondary">ÔºàÊé®Ëñ¶‰∏≠ÈñìÂÄºÔºö{recommendation.flex.avg.toFixed(1)}Ôºâ</Text>
                  </Col>
                  <Col span={24}>
                    <Text type="secondary">
                      <span role="img" aria-label="lightbulb" style={{ marginRight: '4px' }}>üí°</span>
                      ÂàùÂ≠∏ËÄÖÂèØÈÅ∏ÊìáËºÉÁü≠‰∏ÄÁ¥öÔºà‰Ωé5cmÔºâ‰ª•Âà©ÊéßÂà∂ÔºåÈÄ≤ÈöéËÄÖÂèØÈÅ∏ÊìáÂÅèÈï∑‰ª•ÊèêÂçáÈÄüÂ∫¶ËàáÁ©©ÂÆöÊÄß„ÄÇ
                    </Text>
                  </Col>
                </Row>
              </Card>
            </>
          )}
        </Card>
        <div style={{ textAlign: 'center', margin: '20px 0' }}>
          <Text type="secondary">
            <span role="img" aria-label="ski" style={{ marginRight: '4px' }}>üéø</span>
            Ê†πÊìöÊÇ®ÁöÑË∫´È´îÁâπÂæµÂíåÊªëÈõ™È¢®Ê†ºÔºåÁÇ∫ÊÇ®ÈáèË∫´Êé®Ëñ¶ÂêàÈÅ©ÁöÑÊªëÈõ™ÊùøË¶èÊ†º
          </Text>
        </div>
      </Col>
    </Row>
  );
};

export default SnowboardSelector;
