import React, { useState } from 'react';
import { Form, Select, InputNumber, Card, Row, Col } from 'antd';
import { UserInputs, BoardRecommendation, RidingStyle } from '../types/snowboard';

const SnowboardSelector: React.FC = () => {
  const [form] = Form.useForm();
  const [recommendation, setRecommendation] = useState<BoardRecommendation | null>(null);

  // 计算推荐长度范围
  const calculateLengthRange = (inputs: UserInputs): [number, number] => {
    const { region, gender, height, weight } = inputs;

    if (region === 'japan') {
      // 日本地区（身高为主）
      const baseLength = height * 0.9;
      return gender === 'male' 
        ? [baseLength - 5, baseLength + 5]
        : [baseLength - 10, baseLength];
    } else {
      // 台湾地区（体重为主）
      const baseLength = weight * 2.5 + 110;
      return gender === 'male'
        ? [baseLength - 5, baseLength + 5]
        : [baseLength - 10, baseLength];
    }
  };

  // 获取推荐板型
  const getBoardProfile = (style: RidingStyle): string => {
    const profiles: Record<RidingStyle, string> = {
      'all-mountain': 'Hybrid (混合型)',
      'freestyle': 'Rocker (反拱) 或 Flat (平底)',
      'carving': 'Camber (正拱)'
    };
    return profiles[style];
  };

  // 获取推荐硬度
  const getFlexRange = (style: RidingStyle): [number, number] => {
    const flexRanges: Record<RidingStyle, [number, number]> = {
      'all-mountain': [5, 7],
      'freestyle': [1, 5],
      'carving': [6, 10]
    };
    return flexRanges[style];
  };

  const onValuesChange = () => {
    const values = form.getFieldsValue();
    if (values.region && values.gender && values.height && values.weight && values.ridingStyle) {
      const lengthRange = calculateLengthRange(values);
      const [flexMin, flexMax] = getFlexRange(values.ridingStyle);

      setRecommendation({
        lengthRange: {
          min: lengthRange[0],
          max: lengthRange[1]
        },
        profile: getBoardProfile(values.ridingStyle),
        flex: {
          min: flexMin,
          max: flexMax
        }
      });
    }
  };

  return (
    <Row justify="center">
      <Col xs={24} sm={20} md={16} lg={12}>
        <Card title="滑雪板选择器">
          <Form
            form={form}
            layout="vertical"
            onValuesChange={onValuesChange}
          >
            <Form.Item
              name="region"
              label="选择地区"
              rules={[{ required: true, message: '请选择地区' }]}
            >
              <Select>
                <Select.Option value="japan">日本</Select.Option>
                <Select.Option value="taiwan">台湾</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="gender"
              label="性别"
              rules={[{ required: true, message: '请选择性别' }]}
            >
              <Select>
                <Select.Option value="male">男性</Select.Option>
                <Select.Option value="female">女性</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="ridingStyle"
              label="滑雪风格"
              rules={[{ required: true, message: '请选择滑雪风格' }]}
            >
              <Select>
                <Select.Option value="all-mountain">通用</Select.Option>
                <Select.Option value="freestyle">平花</Select.Option>
                <Select.Option value="carving">刻滑</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="height"
              label="身高 (cm)"
              rules={[
                { required: true, message: '请输入身高' },
                { type: 'number', min: 140, max: 200, message: '身高应在140-200cm之间' }
              ]}
            >
              <InputNumber style={{ width: '100%' }} />
            </Form.Item>

            <Form.Item
              name="weight"
              label="体重 (kg)"
              rules={[
                { required: true, message: '请输入体重' },
                { type: 'number', min: 40, max: 120, message: '体重应在40-120kg之间' }
              ]}
            >
              <InputNumber style={{ width: '100%' }} />
            </Form.Item>
          </Form>

          {recommendation && (
            <Card type="inner" title="推荐结果">
              <p>建议雪板长度：{recommendation.lengthRange.min.toFixed(1)} - {recommendation.lengthRange.max.toFixed(1)} cm</p>
              <p>建议板型：{recommendation.profile}</p>
              <p>建议硬度：{recommendation.flex.min} - {recommendation.flex.max}</p>
            </Card>
          )}
        </Card>
      </Col>
    </Row>
  );
};

export default SnowboardSelector;
